<?php\n// Assuming you have a MySQL connection and have run a query\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$query = "SELECT * FROM your_table";\n$result = $mysqli->query($query);\n\nif ($result) {\n    // Fetch all rows as an array of objects\n    $data = $result->fetch_all(MYSQLI_ASSOC);\n    \n    // Serialize the data\n    $serializedData = serialize($data);\n    \n    // Write the serialized string to a file\n    file_put_contents('cached_data.txt', $serializedData);\n    \n    echo "Data has been written to the file.";\n} else {\n    echo "Query failed: " . $mysqli->error;\n}\n\n$mysqli->close();\n?>