public function soapTest() {\n    echo "start <br />";\n    use_soap_error_handler(true);\n\n    try {\n        $client = new SoapClient("https://cwi.rezexchange.com:9991/Service.asmx?wsdl");\n\n        // Define parameters\n        $params = [\n            'PropertyCode' => 'your_property_code', // replace with actual property code\n            'DateFrom' => '2022-01-01T00:00:00', // replace with actual start date in ISO 8601 format\n            'DateTo' => '2022-01-31T23:59:59', // replace with actual end date in ISO 8601 format\n            'RatePlan' => 'your_rate_plan', // replace with actual rate plan\n            'RoomType' => 'your_room_type', // replace with actual room type\n            'PromotionalRates' => false // or true, depending on your requirement\n        ];\n\n        // Call the SOAP method\n        $result = $client->CwiRateDetails($params);\n\n        // Print the result\n        echo '<pre>';\n        print_r($result);\n        echo '</pre>';\n    } catch (SoapFault $e) {\n        echo "SOAP Fault: " . $e->getMessage();\n    }\n\n    echo "<br /> end";\n}