<?php\n\nfunction processSmsResponse($response) {\n    // Define the code and expected digit pattern\n    $code = 'Freetrip';\n    $pattern = '/^' . $code . ' (\d{5})$/i';\n\n    // Validate the response using regex\n    if (preg_match($pattern, $response, $matches)) {\n        // Extract the digits part\n        $digits = str_split($matches[1]);\n\n        // Check if all digits are between 1 and 5\n        foreach ($digits as $digit) {\n            if ($digit < 1 || $digit > 5) {\n                return 'Error: Digits must be between 1 and 5.';\n            }\n        }\n\n        // Return the digits as an array or save them to the DB\n        return $digits;\n    } else {\n        return 'Error: Invalid message format. Expected format: "Freetrip 12345".';\n    }\n}\n\n// Example usage:\n$response = 'Freetrip 12345';\n$result = processSmsResponse($response);\n\nif (is_array($result)) {\n    echo 'Success: ' . implode(', ', $result);\n} else {\n    echo $result;\n}\n?>