function listFtpDirectory($connect, $directory) {\n    $list = ftp_nlist($connect, $directory);\n    if ($list === false) {\n        echo "Failed to list directory: $directory\n";\n    } else {\n        var_dump($list);\n    }\n}\n\n$connect = ftp_connect('example.com');\nif (!$connect) {\n    die('Could not connect to FTP server');\n}\n\n$result = ftp_login($connect, 'username', 'password');\nif (!$result) {\n    die('Could not log in to FTP server');\n}\n\n// Enable passive mode\nftp_pasv($connect, true);\n\necho '<pre>';\nlistFtpDirectory($connect, '');\nlistFtpDirectory($connect, '/outgoing/');\nlistFtpDirectory($connect, '/2689312/');\nlistFtpDirectory($connect, '/2689312/outgoing/');\necho '</pre>';\n\nftp_close($connect);