// Email validation and sanitization\n$email = "example@example.com\r\n";\n$sanitized_email = filter_var($email, FILTER_SANITIZE_EMAIL);\nif (filter_var($sanitized_email, FILTER_VALIDATE_EMAIL)) {\n    echo "Valid email: $sanitized_email";\n} else {\n    echo "Invalid email";\n}\n\n// String sanitization\n$dirty_string = "<h1>Hello</h1><script>alert('XSS');</script>";\n$sanitized_string = filter_var($dirty_string, FILTER_SANITIZE_STRING, FILTER_FLAG_NO_ENCODE_QUOTES);\necho $sanitized_string; // Outputs: Helloalert('XSS');