<?php\n// Path to the ffmpeg binary\n$ffmpegPath = '/usr/bin/ffmpeg';\n\n// Input and output files\n$inputFile = '/path/to/input.mp4';\n$outputFile = '/path/to/output.mp4';\n\n// Validate and sanitize file paths (basic example, consider more robust validation)\nif (!preg_match('/^[a-zA-Z0-9_\-\/]+\.mp4$/', $inputFile) || !preg_match('/^[a-zA-Z0-9_\-\/]+\.mp4$/', $outputFile)) {\n    die('Invalid file path');\n}\n\n// Escape arguments\n$escapedInputFile = escapeshellarg($inputFile);\n$escapedOutputFile = escapeshellarg($outputFile);\n\n// Build the command\n$command = "$ffmpegPath -i $escapedInputFile $escapedOutputFile 2>&1";\n\n// Execute the command and capture output\n$output = [];\n$returnVar = 0;\nexec($command, $output, $returnVar);\n\n// Check for errors\nif ($returnVar !== 0) {\n    // Log the error or handle it as needed\n    error_log('FFmpeg execution failed: ' . implode("\n", $output));\n    die('Error encoding video');\n}\n\n// Continue with the rest of your code\necho 'Video encoded successfully';\n?>