<?php\nsession_start();\n$db = new PDO('sqlite:/path/to/your/database.db');\n$db->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n$session_id = session_id();\n$json_data = $_POST['json_data'] ?? '';\n\nif (!empty($json_data) && isValidJson($json_data)) {\n    try {\n        $stmt = $db->prepare("SELECT COUNT(*) FROM q_and_a_storage WHERE php_session_id = :session_id");\n        $stmt->execute([':session_id' => $session_id]);\n        $exists = $stmt->fetchColumn();\n\n        if ($exists) {\n            $stmt = $db->prepare("UPDATE q_and_a_storage SET json_storage = :json_data WHERE php_session_id = :session_id");\n        } else {\n            $stmt = $db->prepare("INSERT INTO q_and_a_storage (php_session_id, json_storage) VALUES (:session_id, :json_data)");\n        }\n\n        $stmt->execute([':session_id' => $session_id, ':json_data' => $json_data]);\n        echo json_encode(['status' => 'success']);\n    } catch (Exception $e) {\n        echo json_encode(['status' => 'error', 'message' => $e->getMessage()]);\n    }\n} else {\n    echo json_encode(['status' => 'error', 'message' => 'Invalid JSON data']);\n}\n\nfunction isValidJson($data) {\n    json_decode($data);\n    return (json_last_error() == JSON_ERROR_NONE);\n}\n?>