<?php\n    class Zend_View_Helper_ImageHelper extends Zend_View_Helper_Abstract\n    {\n        public function imageHelper($width, $height)\n        {\n            // Create the image resource\n            $image = imagecreatetruecolor($width, $height);\n\n            // Allocate a color for the image (white background)\n            $white = imagecolorallocate($image, 255, 255, 255);\n            imagefill($image, 0, 0, $white);\n\n            // Draw something on the image (e.g., a rectangle)\n            $black = imagecolorallocate($image, 0, 0, 0);\n            imagerectangle($image, 10, 10, $width - 10, $height - 10, $black);\n\n            // Capture the output buffer\n            ob_start();\n            imagepng($image);\n            $imageData = ob_get_clean();\n\n            // Clean up\n            imagedestroy($image);\n\n            // Encode the data into Base64 and return as data URI\n            return 'data:image/png;base64,' . base64_encode($imageData);\n        }\n    }