class ItemController extends Zend_Controller_Action\n{\n    public function indexAction()\n    {\n        $this->view->tab1Content = $this->getTab1Content();\n        $this->view->tab2Content = $this->getTab2Content();\n        // Add more tabs as needed\n    }\n\n    protected function getTab1Content()\n    {\n        // Fetch data for tab 1\n        return $this->view->partial('item/_tab1.phtml', ['data' => $dataForTab1]);\n    }\n\n    protected function getTab2Content()\n    {\n        // Fetch data for tab 2\n        return $this->view->partial('item/_tab2.phtml', ['data' => $dataForTab2]);\n    }\n}