<?php\nfunction isBuggyIE() {\n    $userAgent = $_SERVER['HTTP_USER_AGENT'];\n    if (strpos($userAgent, 'MSIE ') !== false) {\n        // Extract the version number\n        preg_match('/MSIE (\d+\.\d+);/', $userAgent, $matches);\n        if (isset($matches[1])) {\n            $version = floatval($matches[1]);\n            if ($version <= 6) {\n                return true;\n            }\n        }\n    }\n    return false;\n}\n\nif (!isBuggyIE()) {\n    ob_start('ob_gzhandler');\n} else {\n    ob_start();\n}\n\n// Your page content here\n\nob_end_flush();\n?>