$blahOptions = 'option_1=1';\nBlahString($blahOptions);\n\nfunction BlahString($options = '') {\n    $defaults = array(\n        'option_1' => false,\n        'option_2' => 'blah',\n    );\n    parse_str($options, $parsedOptions);\n    $options = array_merge($defaults, $parsedOptions); // Merges defaults with parsed options\n    // Now $options contains all parameters with defaults applied\n}