<?php\n// Database connection\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Fetch user settings from the database\n$sql = "SELECT * FROM user_settings WHERE user_id = ?";\n$stmt = $conn->prepare($sql);\n$user_id = 1; // Replace with dynamic user ID\n$stmt->bind_param("i", $user_id);\n$stmt->execute();\n$result = $stmt->get_result();\n$settings = $result->fetch_assoc();\n\necho '<form method="post" action="update_settings.php">';\necho '<table>';\n\n// Assuming 'setting1' and 'setting2' are the column names in your table\necho '<tr>';\necho '<td>1</td>';\necho '<td><input name="settings[setting1]" type="checkbox" '. ($settings['setting1'] ? 'checked' : '') .'></td>';\necho '</tr>';\n\necho '<tr>';\necho '<td>2</td>';\necho '<td><input name="settings[setting2]" type="checkbox" '. ($settings['setting2'] ? 'checked' : '') .'></td>';\necho '</tr>';\n\necho '</table>';\necho '<input name="update" type="submit" value="Update">';\necho '</form>';\n\n$conn->close();\n?>