<?php\n$string = <<<EOD\n<p>My string looks like so</p>\n\n<blockquote>\n  <p>February 2009</p>\n  \n  <p>bla bla</p>\n  \n  <p>March 2009</p>\n  \n  <p>doo daa bla lbla</p>\n  \n  <p>September 2009</p>\n</blockquote>\nEOD;\n\n$regex = '/<p>([^<]+ \d{4})<\/p>\s*(<p>.*?<\/p>)/is';\npreg_match_all($regex, $string, $matches, PREG_SET_ORDER);\n\nforeach ($matches as $match) {\n    echo "Matched Date: " . $match[1] . "\n";\n    echo "Content: " . $match[2] . "\n\n";\n}\n?>