function secondToLastOccurrence($string, $char) {\n    // Find the last occurrence of the character\n    $lastPos = strrpos($string, $char);\n    \n    // If the character is not found or is found only once, return false\n    if ($lastPos === false) {\n        return false;\n    }\n    \n    // Find the substring before the last occurrence\n    $substring = substr($string, 0, $lastPos);\n    \n    // Find the second-to-last occurrence within that substring\n    $secondToLastPos = strrpos($substring, $char);\n    \n    return $secondToLastPos;\n}\n\n// Example usage\n$string = "This is a test string.";\n$char = 't';\n$position = secondToLastOccurrence($string, $char);\n\nif ($position !== false) {\n    echo "The second-to-last occurrence of '$char' is at position $position.";\n} else {\n    echo "The character '$char' does not occur at least twice in the string.";\n}