if ($cmd == "submitinfo") {\n    // Prepare the SELECT query\n    $usernameQuery = "SELECT username FROM USERS WHERE username = ?";\n    if ($stmt = $con->prepare($usernameQuery)) {\n        $stmt->bind_param("s", $username);\n        $stmt->execute();\n        $result = $stmt->get_result();\n        $checkUsername = null;\n\n        if ($row = $result->fetch_assoc()) {\n            $checkUsername = $row['username'];\n        }\n        $stmt->close();\n    } else {\n        echo "Error in SELECT query: " . $con->error;\n        exit;\n    }\n\n    if ($checkUsername === null) {\n        // Prepare the INSERT query\n        $userQuery = "INSERT INTO USERS VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)";\n        if ($userInfo = $con->prepare($userQuery)) {\n            $userInfo->bind_param("ssssssssssssssssssss", $username, $firstname, $lastname, $flaggedauctions, $lastauction, $street1, $city1, $postcode1, $street2, $city2, $postcode2, $phone, $mobilephone, $fax, $email, $website, $bank, $banknumber, $accountnumber, $comments);\n            $userInfo->execute();\n            $userInfo->close();\n            echo "true";\n        } else {\n            echo "Error in INSERT query: " . $con->error;\n        }\n    } else if ($checkUsername == $username) {\n        // Prepare the UPDATE query\n        $userQuery = "UPDATE USERS SET firstname = ?, lastname = ?, flaggedauctions = ?, lastauction = ?, street1 = ?, city1 = ?, postcode1 = ?, street2 = ?, city2 = ?, postcode2 = ?, phone = ?, mobilephone = ?, fax = ?, email = ?, website = ?, bank = ?, banknumber = ?, accountnumber = ? WHERE username = ?";\n        if ($userInfo = $con->prepare($userQuery)) {\n            $userInfo->bind_param("sssssssssssssssssss", $firstname, $lastname, $flaggedauctions, $lastauction, $street1, $city1, $postcode1, $street2, $city2, $postcode2, $phone, $mobilephone, $fax, $email, $website, $bank, $banknumber, $accountnumber, $username);\n            $userInfo->execute();\n            $userInfo->close();\n            echo "true";\n        } else {\n            echo "Error in UPDATE query: " . $con->error;\n        }\n    }\n}