// File: application/views/helpers/FormButton.php\n\nclass Zend_View_Helper_FormButtonWithSpan extends Zend_View_Helper_FormButton\n{\n    public function formButtonWithSpan($name, $value = null, $attribs = null, $options = null)\n    {\n        $attribs['escape'] = false; // Ensure the value is not escaped\n        $value = '<span>' . $this->view->escape($value) . '</span>';\n        \n        return parent::formButton($name, $value, $attribs, $options);\n    }\n}