// Example function to submit a modification\nfunction submitModification($event_id, $name, $start_date, $guest, $modified_by) {\n    $conn = new mysqli("localhost", "username", "password", "database");\n\n    $stmt = $conn->prepare("INSERT INTO pending_events (event_id, name, start_date, guest, modified_by) VALUES (?, ?, ?, ?, ?)");\n    $stmt->bind_param("issii", $event_id, $name, $start_date, $guest, $modified_by);\n    $stmt->execute();\n    $stmt->close();\n    $conn->close();\n}\n\n// Example function to approve a modification\nfunction approveModification($pending_id) {\n    $conn = new mysqli("localhost", "username", "password", "database");\n\n    // Fetch pending modification\n    $stmt = $conn->prepare("SELECT event_id, name, start_date, guest FROM pending_events WHERE id = ?");\n    $stmt->bind_param("i", $pending_id);\n    $stmt->execute();\n    $stmt->bind_result($event_id, $name, $start_date, $guest);\n    $stmt->fetch();\n    $stmt->close();\n\n    // Apply modification to events table\n    $stmt = $conn->prepare("UPDATE events SET name = ?, start_date = ?, guest = ? WHERE id = ?");\n    $stmt->bind_param("ssii", $name, $start_date, $guest, $event_id);\n    $stmt->execute();\n    $stmt->close();\n\n    // Remove pending modification\n    $stmt = $conn->prepare("DELETE FROM pending_events WHERE id = ?");\n    $stmt->bind_param("i", $pending_id);\n    $stmt->execute();\n    $stmt->close();\n\n    $conn->close();\n}