<?php\n$xmlString = <<<XML\n<xml>\n    <pointless_node/>\n    <seq>\n        <keyword>diamond ring</keyword>\n        <keyword>ruby necklace</keyword>\n        <keyword>mens watch</keyword>\n    </seq>\n    <some_node/>\n</xml>\nXML;\n\n$doc = new DOMDocument();\n$doc->loadXML($xmlString);\n$keywords = [];\n\n$keywordNodes = $doc->getElementsByTagName('keyword');\nforeach ($keywordNodes as $node) {\n    $keywords[] = $node->nodeValue;\n}\n\nprint_r($keywords);\n?>