class Test\n{\n    public function testOut($methodName, ...$params) {\n        if(method_exists($this, $methodName)) {\n            $this->$methodName(...$params);\n        } else {\n            echo "Method $methodName does not exist.";\n        }\n    }\n\n    private function a() {\n        echo "a";\n    }\n\n    private function b($param) {\n        echo "b with param: $param";\n    }\n}\n\n$testObj = new Test();\n$testObj->testOut("a"); // Outputs: a\n$testObj->testOut("b", "example"); // Outputs: b with param: example