global $post;\n$posts = get_posts(array('numberposts' => $limit));\n\nforeach ($posts as $post) {\n    setup_postdata($post);\n    \n    // Get the title without printing it\n    $title = get_the_title();\n    \n    // Get the permalink without printing it\n    $permalink = get_permalink();\n    \n    // Get the content without printing it\n    $content = apply_filters('the_content', get_the_content());\n    \n    // Now you can use $title, $permalink, and $content as needed without displaying them\n    // For example, you could process them or save them to a variable\n    \n    // Example processing (printing for demonstration, but not necessary)\n    echo "<a href='$permalink'>$title</a><br>";\n    echo htmlentities($content, ENT_COMPAT, "UTF-8") . "<br>";\n}\n\n// Reset post data to ensure global $post is restored\nwp_reset_postdata();