<?php\n$cat = $_POST['cat']; // trait set for textbox inputs\nforeach(array_keys($cat) as $key) {\n    $cat[$key] = explode(",", str_replace(' ', '', $cat[$key]));\n}\n\nfunction showCombinations($string, $traits, $i) {\n    if ($i >= count($traits)) {\n        echo trim($string) . "\n";\n    } else {\n        foreach ($traits[$i] as $trait) {\n            showCombinations("$string$trait", $traits, $i + 1);\n        }\n    }\n}\n?>\n\n<form name="form1" method="post" action="">\n    <textarea><?php ob_start(); showCombinations('', $cat, 0); $output = ob_get_clean(); echo htmlspecialchars($output); ?></textarea>\n</form>