<?php\n\nclass A {\n    public $myName;\n\n    public function __set($name, $value) {\n        $this->$name = $value;\n    }\n}\n\nclass B extends A {\n    private $anotherA;\n\n    public function __construct() {\n        $this->anotherA = new A();\n        $this->anotherA->myName = 'stackoverflow';\n    }\n\n    public function getAnotherA() {\n        return $this->anotherA;\n    }\n}\n\n// Example usage:\n$b = new B();\necho $b->getAnotherA()->myName; // Outputs "stackoverflow"\n\n?>