function getIndexByUsername($array, $username) {\n    foreach ($array as $index => $subArray) {\n        if (isset($subArray['username']) && $subArray['username'] === $username) {\n            return $index;\n        }\n    }\n    return null; // Return null if username is not found\n}\n\n$array = array(\n    array('username' => 'user1'),\n    array('username' => 'user2'),\n);\n\n$username = 'user1';\n$index = getIndexByUsername($array, $username);\n\nif ($index !== null) {\n    echo "The index of the user is: " . $index;\n} else {\n    echo "Username not found.";\n}