// Input password from user\n$inputPasswd = $_POST['password'];\n\n// Ensure the password is safely passed to the command line\n$newPasswd = escapeshellarg($inputPasswd);\n\n// Build the command line\n$cmdline = escapeshellcmd($htpasswd) . ' ' . escapeshellarg($passwd_file) . ' ' . escapeshellarg($username) . ' ' . $newPasswd;\n\n// Execute the command\nexec($cmdline, $output, $return_var);\n\n// Check for success or failure\nif ($return_var === 0) {\n    echo "Password updated successfully.";\n} else {\n    echo "Password update failed.";\n}