<?php\n$servername = "your_servername";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n$batchSize = 10; // Number of emails to send per batch\n\n$sql = "SELECT id, email, subject, message FROM email_queue WHERE status = 'pending' LIMIT $batchSize";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    while ($row = $result->fetch_assoc()) {\n        $email = $row["email"];\n        $subject = $row["subject"];\n        $message = $row["message"];\n        $headers = "From: your_email@example.com";\n\n        if (mail($email, $subject, $message, $headers)) {\n            // Update email status to 'sent'\n            $updateSql = "UPDATE email_queue SET status = 'sent' WHERE id = " . $row["id"];\n            $conn->query($updateSql);\n        }\n    }\n} else {\n    echo "No emails to send.";\n}\n\n$conn->close();\n?>