<?php\n// Ignore user aborts and allow the script to run forever\nignore_user_abort(true);\nset_time_limit(0);\n\n$fp = fopen('loop.log', 'a');\nif (!$fp) {\n    error_log('Failed to open log file');\n    exit(1);\n}\n\nfwrite($fp, date('Y-m-d H:i:s') . ' Started' . PHP_EOL);\n\nwhile (true) {\n    fwrite($fp, date('Y-m-d H:i:s') . ' Looped' . PHP_EOL);\n    if (file_exists('loop.stop')) {\n        break;\n    }\n\n    // Insert your actual functionality here\n    // ...\n\n    // Sleep for 100 seconds to reduce CPU load\n    sleep(100);\n}\n\nfwrite($fp, date('Y-m-d H:i:s') . ' Stopped' . PHP_EOL);\nfclose($fp);