<?php\n$galleries = array(1, 2, 5);\n\n// Create a string of placeholders for the prepared statement\n$placeholders = implode(',', array_fill(0, count($galleries), '?'));\n\n// Prepare the SQL query\n$sql = "SELECT * FROM galleries WHERE id IN ($placeholders)";\n\n// Create a new MySQLi connection\n$conn = new mysqli('hostname', 'username', 'password', 'database');\n\n// Check the connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Prepare the statement\n$stmt = $conn->prepare($sql);\n\n// Bind the parameters\n$types = str_repeat('i', count($galleries)); // Assuming IDs are integers\n$stmt->bind_param($types, ...$galleries);\n\n// Execute the statement\n$stmt->execute();\n\n// Get the result\n$result = $stmt->get_result();\n\n// Fetch the data\nwhile ($row = $result->fetch_assoc()) {\n    print_r($row);\n}\n\n// Close the statement and connection\n$stmt->close();\n$conn->close();\n?>