<?php\nsession_start();\n\nif (isset($_SESSION['form_submitted']) && $_SESSION['form_submitted'] === true && isset($_GET['file'])) {\n    $file = $_GET['file'];\n    $filepath = __DIR__ . '/pdf/' . $file;\n\n    if (file_exists($filepath)) {\n        header('Content-Type: application/pdf');\n        header('Content-Disposition: attachment; filename="' . basename($filepath) . '"');\n        readfile($filepath);\n        exit;\n    } else {\n        echo "File not found.";\n    }\n} else {\n    echo "Unauthorized access.";\n}