// Sample PHP code to query Elasticsearch\nrequire 'vendor/autoload.php';\n\nuse Elasticsearch\ClientBuilder;\n\n$client = ClientBuilder::create()->build();\n\n$params = [\n    'index' => 'my_index',\n    'body'  => [\n        'query' => [\n            'match' => [\n                'content' => 'search term'\n            ]\n        ]\n    ]\n];\n\n$response = $client->search($params);\nprint_r($response);