<?php\ntry {\n    $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'your_username', 'your_password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Insert the row into the `reunie` table\n    $stmt = $pdo->prepare("INSERT INTO `reunie` (`naam`, `van`, `nooiensvan`, `selfoon`, `email`, `bywoon`, `metgesel_naam`, `spesifieke_onderwysers`, `spesifieke_musiek`, `bydrae`, `voorstelle`) VALUES (:naam, :van, :nooiensvan, :selfoon, :email, :bywoon, :metgesel, :spesifiekeOnderwysers, :musiek, :bydrae, :voorstelle)");\n    $stmt->execute([\n        ':naam' => $naam,\n        ':van' => $van,\n        ':nooiensvan' => $nooiensvan,\n        ':selfoon' => $selfoon,\n        ':email' => $email,\n        ':bywoon' => $bywoon,\n        ':metgesel' => $metgesel,\n        ':spesifiekeOnderwysers' => $spesifiekeOnderwysers,\n        ':musiek' => $musiek,\n        ':bydrae' => $bydrae,\n        ':voorstelle' => $voorstelle\n    ]);\n\n    // Get the last inserted id\n    $lastInsertId = $pdo->lastInsertId();\n\n    // Use the last inserted id for your other logic\n    $stmt = $pdo->prepare("INSERT INTO another_table (reunie_id) VALUES (:reunie_id)");\n    $stmt->execute([':reunie_id' => $lastInsertId]);\n\n} catch (PDOException $e) {\n    echo 'Error: ' . $e->getMessage();\n}\n?>