<?php\n// Database configuration\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Set the correct charset\n$conn->set_charset("utf8mb4");\n\n// Example data to insert\n$show = "That '70s Show";\n\n// Using prepared statements to insert data\n$stmt = $conn->prepare("INSERT INTO shows (title) VALUES (?)");\n$stmt->bind_param("s", $show);\n$stmt->execute();\n$stmt->close();\n\n// Example search query\n$search = "That '70s Show";\n\n// Using prepared statements to search data\n$stmt = $conn->prepare("SELECT * FROM shows WHERE title LIKE ?");\n$search_param = "%" . $conn->real_escape_string($search) . "%";\n$stmt->bind_param("s", $search_param);\n$stmt->execute();\n$result = $stmt->get_result();\n\n// Fetch and display results\nwhile ($row = $result->fetch_assoc()) {\n    echo "Found show: " . $row['title'] . "<br>";\n}\n\n$stmt->close();\n$conn->close();\n?>