<?php\nfunction getMediaFireDirectLink($mediaFireUrl) {\n    // Initialize a cURL session\n    $ch = curl_init();\n\n    // Set the URL\n    curl_setopt($ch, CURLOPT_URL, $mediaFireUrl);\n\n    // Return the transfer as a string\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n\n    // Execute the session and store the contents in a variable\n    $output = curl_exec($ch);\n\n    // Close the cURL session\n    curl_close($ch);\n\n    // Check if the cURL request was successful\n    if ($output === false) {\n        return "Error fetching the URL";\n    }\n\n    // Use DOMDocument to parse the HTML\n    $dom = new DOMDocument();\n    @$dom->loadHTML($output);\n\n    // Use DOMXPath to query the DOM\n    $xpath = new DOMXPath($dom);\n    $downloadLinkNode = $xpath->query("//div[@id='download_link']//a");\n\n    // Check if the download link was found\n    if ($downloadLinkNode->length > 0) {\n        $directLink = $downloadLinkNode->item(0)->getAttribute('href');\n        return $directLink;\n    } else {\n        return "Direct download link not found.";\n    }\n}\n\n// Example usage\n$mediaFireUrl = "https://www.mediafire.com/file/yourfile";\n$directLink = getMediaFireDirectLink($mediaFireUrl);\necho "Direct Download Link: " . $directLink;\n?>