<?php\nif ($_SERVER['REQUEST_METHOD'] == 'POST' && isset($_POST['ssn'])) {\n    $encryptedSSN = $_POST['ssn'];\n\n    // Decrypt the SSN using the same key and IV\n    $key = '1234567890123456'; // Replace with your own key\n    $iv = '1234567890123456'; // Replace with your own IV\n    $decryptedSSN = openssl_decrypt(base64_decode($encryptedSSN), 'aes-128-cbc', $key, OPENSSL_RAW_DATA, $iv);\n\n    // Make the external web service call with the decrypted SSN\n    $url = "https://external-web-service.com/api";\n    $data = array('ssn' => $decryptedSSN);\n\n    $options = array(\n        'http' => array(\n            'header'  => "Content-type: application/x-www-form-urlencoded\r\n",\n            'method'  => 'POST',\n            'content' => http_build_query($data),\n        ),\n    );\n    $context  = stream_context_create($options);\n    $result = file_get_contents($url, false, $context);\n\n    if ($result === FALSE) {\n        // Handle error\n        echo json_encode(array('error' => 'Unable to complete request'));\n    } else {\n        // Return the result\n        echo $result;\n    }\n}\n?>