<?php\n// Example variables\n$var1 = "Hello";\n$var2 = 123;\n$var3 = array('a', 'b', 'c');\n\n// Get all defined variables\n$defined_vars = get_defined_vars();\n\n// Display all defined variables\nforeach ($defined_vars as $varname => $value) {\n    if ($varname != 'defined_vars') { // To avoid displaying the $defined_vars variable itself\n        echo "<b>" . htmlspecialchars($varname) . "</b> = " . htmlspecialchars(print_r($value, true)) . "<br/>";\n    }\n}\n?>