<?php\n/**\n * Generate a keyed hash value using the HMAC method.\n *\n * @param string $q The input string (this may be an MD5 or SHA hash).\n * @param string $seed The seed string.\n * @return string The generated string p.\n */\nfunction genKey($q, $seed) {\n    return hash_hmac('sha256', $q, $seed);\n}\n\n/**\n * Check if a given string p was generated from string q using the same seed.\n *\n * @param string $p The generated string to check.\n * @param string $q The original input string.\n * @param string $seed The seed string used in the generation.\n * @return bool True if the string p was generated from string q using the seed, false otherwise.\n */\nfunction checkKey($p, $q, $seed) {\n    $generated_p = genKey($q, $seed);\n    return hash_equals($generated_p, $p);\n}\n\n// Example usage\n$q = 'example_string';\n$seed = 'secret_seed';\n$p = genKey($q, $seed);\n\necho "Generated key: " . $p . "\n";\n\n$isValid = checkKey($p, $q, $seed);\necho "Is valid: " . ($isValid ? 'true' : 'false') . "\n";\n?>