if (isset($_POST['submit'])) {\n    $browser = $_SERVER['HTTP_USER_AGENT'];\n    $page = $_POST['page'];\n    $email = $_POST['email'];\n    $error = $_POST['error'];\n\n    $message  = "<html><body>";\n    $message .= "Email: $email<br>";\n    $message .= "Page: $page<br>";\n    $message .= "OS/Browser: $browser<br>";\n    $message .= "Error: $error<br>";\n    $message .= "</body></html>";\n\n    $headers  = "MIME-Version: 1.0\r\n";\n    $headers .= "Content-type: text/html; charset=iso-8859-1\r\n";\n    $headers .= "From: code@website.com\r\n";\n    $headers .= "Reply-To: $email\r\n";\n    $headers .= "X-Priority: 1\r\n";\n\n    $subject  = "[ERROR REPORT] Page: " . $page;\n\n    mail("myEmail@gmail.com", $subject, $message, $headers);\n\n    $mesg = "Thank you for your report!";\n}\n?>