<?php\nclass DynamicNav {\n    private $conn;\n    private $database;\n    \n    public function __construct($host, $user, $password, $database) {\n        $this->database = $database;\n        $this->conn = new mysqli($host, $user, $password, $database);\n        \n        if ($this->conn->connect_error) {\n            die("Connection failed: " . $this->conn->connect_error);\n        }\n    }\n    \n    public function generateNavigation() {\n        echo '<div class="dynamic_nav_head">Navigation</div>';\n        echo '<div class="dynamic_nav">';\n        \n        $query = "SHOW TABLES FROM `$this->database`";\n        if ($result = $this->conn->query($query)) {\n            while ($row = $result->fetch_array()) {\n                $table = $row[0];\n                if ($table != 'includes') {\n                    $this->generateNavItem($table);\n                }\n            }\n            $result->free();\n        }\n        \n        echo '</div>';\n    }\n    \n    private function generateNavItem($table) {\n        $tableName = ucwords($table);\n        echo "<div class='cat'>$tableName</div>";\n        echo "<div class='cat_item'>";\n        echo "<a href='?page=read&section=" . htmlspecialchars($table) . "'>View $tableName</a>";\n        echo "</div>";\n        echo "<div class='cat_item'>";\n        echo "<a href='?page=add&section=" . htmlspecialchars($table) . "'>Add New $tableName</a>";\n        echo "</div>";\n    }\n    \n    public function __destruct() {\n        $this->conn->close();\n    }\n}\n\n// Usage\n$host = 'your_host';\n$user = 'your_user';\n$password = 'your_password';\n$database = 'your_database';\n\n$nav = new DynamicNav($host, $user, $password, $database);\n$nav->generateNavigation();\n?>