<?php\n$tolerance = 0.1; // Tolerance for height to width ratio\n$found = false; // Flag to check if a suitable image is found\n\nforeach (glob("*.jpg") as $filename) {\n    $info = getimagesize($filename);\n    if ($info) {\n        $width = $info[0];\n        $height = $info[1];\n        $ratio = $width / $height;\n\n        // Check if the ratio is close to 1 (square-like) within the tolerance\n        if (abs($ratio - 1) <= $tolerance) {\n            echo "$filename size " . filesize($filename) . "<br>";\n            echo '<img src="'.$filename.'" height=150px><br>';\n            $found = true;\n            break;\n        }\n    }\n}\n\n// If no suitable image is found, display a default image\nif (!$found) {\n    echo 'No suitable image found. Displaying default image.<br>';\n    echo '<img src="default.jpg" height=150px><br>'; // Ensure 'default.jpg' exists in the directory\n}\n?>