<?php\n$value = 123; // Your value to pass to C++\n\n// Use `proc_open` to execute the C++ program\n$descriptorspec = [\n    0 => ["pipe", "r"],  // stdin\n    1 => ["pipe", "w"],  // stdout\n    2 => ["pipe", "w"]   // stderr\n];\n\n$process = proc_open('/path/to/your/cpp_program', $descriptorspec, $pipes);\n\nif (is_resource($process)) {\n    // Pass the value to C++ program\n    fwrite($pipes[0], $value);\n    fclose($pipes[0]);\n\n    // Get the result from C++ program\n    $result = stream_get_contents($pipes[1]);\n    fclose($pipes[1]);\n\n    // Close the process\n    proc_close($process);\n\n    echo "The result from C++: " . $result;\n} else {\n    echo "Failed to execute C++ program.";\n}\n?>