<?php\n// Assuming you have a function to connect to your database\n$connection = new mysqli("host", "user", "password", "database");\n\n// Change the order of item 'one' to 1 and 'two' to 2\n$updateOrderOne = "UPDATE items SET `order` = 1 WHERE id = 1";\n$updateOrderTwo = "UPDATE items SET `order` = 2 WHERE id = 2";\n$updateOrderThree = "UPDATE items SET `order` = 3 WHERE id = 3";\n\nif ($connection->query($updateOrderOne) === TRUE &&\n    $connection->query($updateOrderTwo) === TRUE &&\n    $connection->query($updateOrderThree) === TRUE) {\n    echo "Order updated successfully";\n} else {\n    echo "Error updating order: " . $connection->error;\n}\n\n$connection->close();\n?>