header('Content-type: application/json; charset=ISO-8859-9');\n\n$response = array("name" => "\u00fe\u00fd\u00f0\u00f6\u00e7");\n$json = json_encode($response);\n\n// Decode JSON to PHP array\n$array = json_decode($json, true);\n\n// Convert strings within the array to ISO-8859-9\nforeach ($array as $key => $value) {\n    $array[$key] = mb_convert_encoding($value, "ISO-8859-9", "UTF-8");\n}\n\n// Re-encode the array to JSON\n$json = json_encode($array);\n\necho $json;