<?php\n// Database configuration\n$dsn = 'mysql:host=your_host;dbname=your_dbname';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    // Create a PDO instance\n    $pdo = new PDO($dsn, $username, $password);\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Array of data to be inserted\n    $data = [\n        ['value1_1', 'value1_2', 'value1_3'],\n        ['value2_1', 'value2_2', 'value2_3'],\n        // Add more records as needed\n    ];\n\n    // Prepare the SQL statement with placeholders\n    $sql = "INSERT INTO your_table_name (column1, column2, column3) VALUES (?, ?, ?)";\n    $stmt = $pdo->prepare($sql);\n\n    // Define the batch size\n    $batchSize = 1000;\n    $counter = 0;\n\n    // Begin the transaction\n    $pdo->beginTransaction();\n\n    foreach ($data as $row) {\n        $stmt->execute($row);\n        $counter++;\n\n        // If the batch size is reached, commit the transaction and start a new one\n        if ($counter % $batchSize === 0) {\n            $pdo->commit();\n            $pdo->beginTransaction();\n        }\n    }\n\n    // Commit any remaining transactions\n    $pdo->commit();\n    echo "Data inserted successfully.";\n\n} catch (PDOException $e) {\n    // Rollback transaction if an error occurs\n    if ($pdo->inTransaction()) {\n        $pdo->rollBack();\n    }\n    echo "Error: " . $e->getMessage();\n}\n?>