<?php\n// Shared secret key\n$secret_key = 'your_shared_secret_key';\n\n// Get the posted data\n$data = $_POST['data'];\n$hash = $_POST['hash'];\n\n// Recreate the hash to verify\n$calculated_hash = hash_hmac('sha256', $data, $secret_key);\n\n// Compare the hashes\nif (hash_equals($calculated_hash, $hash)) {\n    // Hashes match, process the data\n    $received_data = json_decode($data, true);\n    // Process the received data\n    echo 'Data received and verified';\n} else {\n    // Hashes do not match, possible tampering\n    echo 'Invalid data';\n}\n?>