<?php\n// Assuming $userInput is the input from the user\n$userInput = $_GET['user-input'];\n\n// Establish a connection to the SQL Server database\n$serverName = "your_server_name";\n$connectionOptions = array(\n    "Database" => "your_database_name",\n    "Uid" => "your_username",\n    "PWD" => "your_password"\n);\n\n// Connect using SQL Server's PDO driver\n$conn = new PDO("sqlsrv:server=$serverName;Database=" . $connectionOptions['Database'], $connectionOptions['Uid'], $connectionOptions['PWD']);\n$conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n// Prepare the SQL query with parameters\n$sql = "SELECT f_name, l_name, (f_name + ' ' + l_name) AS full_name\n        FROM users_table\n        WHERE f_name = :userInput\n           OR l_name = :userInput\n           OR (f_name + ' ' + l_name) = :userInput";\n\n// Prepare and execute the statement\n$stmt = $conn->prepare($sql);\n$stmt->bindParam(':userInput', $userInput);\n$stmt->execute();\n\n// Fetch the results\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n// Output the results\nforeach ($results as $row) {\n    echo "First Name: " . htmlspecialchars($row['f_name']) . "<br>";\n    echo "Last Name: " . htmlspecialchars($row['l_name']) . "<br>";\n    echo "Full Name: " . htmlspecialchars($row['full_name']) . "<br>";\n}\n?>