class YourController extends Zend_Controller_Action\n{\n    public function __call($method, $args)\n    {\n        // Check if the called method is an action\n        if ('Action' == substr($method, -6)) {\n            // Handle the non-existing action\n            // For example, you can forward to a 404 error page\n            $this->_forward('not-found', 'error'); // Forwarding to the 'not-found' action in 'error' controller\n        } else {\n            // If it is not an action, call the parent __call method\n            parent::__call($method, $args);\n        }\n    }\n}