function unzip($zipFilePath, $extractTo) {\n    $zip = new ZipFile();\n    $zip->open($zipFilePath);\n    \n    foreach ($zip->file() as $entry) {\n        $entryName = $entry->getName();\n        $entryPath = $extractTo . DIRECTORY_SEPARATOR . $entryName;\n        \n        if ($entry->isDirectory()) {\n            if (!is_dir($entryPath)) {\n                mkdir($entryPath, 0755, true);\n            }\n        } else {\n            $entryStream = $entry->getStream();\n            $fileContents = stream_get_contents($entryStream);\n            fclose($entryStream);\n            \n            file_put_contents($entryPath, $fileContents);\n        }\n    }\n    \n    $zip->close();\n}\n\n// Usage example\n$zipFilePath = 'path/to/your/zipfile.zip';\n$extractTo = 'path/to/extract/';\n\nif (unzip($zipFilePath, $extractTo)) {\n    echo 'Unzipped successfully!';\n} else {\n    echo 'Failed to unzip.';\n}\n?>