<?php\nfunction isHostAlive($host, $port = 80, $timeout = 10) {\n    $connection = @fsockopen($host, $port, $errno, $errstr, $timeout);\n    \n    if ($connection) {\n        fclose($connection);\n        return true;\n    } else {\n        return false;\n    }\n}\n\n$host = 'example.com';\nif (isHostAlive($host)) {\n    echo "Host is alive.";\n} else {\n    echo "Host is not responding.";\n}\n?>