private function scan_cleanup($scan) {\n    // Only if the scan was not reviewed\n    if ($scan['reviewed'] == 0) {\n        // Change reviewed status to see if there is a duplicate\n        $scan['reviewed'] = 1; // Use assignment operator\n\n        // Return false to remove this copy (and keep reviewed)\n        foreach ($this->scans as $existingScan) {\n            if ($existingScan['patientid'] == $scan['patientid'] && $existingScan['reviewed'] == 1) {\n                return false;\n            }\n        }\n    }\n    return true;\n}