class Worker\n{\n    public function goToWork()\n    {\n        $isInThatMood = rand(0, 1); // Randomly simulating the mood\n        if (!$isInThatMood) {\n            throw new Exception("Worker is not in the mood to work.");\n        }\n        return true;\n    }\n}\n\n$worker = new Worker;\ntry {\n    if ($worker->goToWork()) {\n        echo "Good.";\n    }\n} catch (Exception $e) {\n    if (date('l', time()) == 'Sunday') {\n        echo "Fine, you don't have to work on Sundays...";\n    } else {\n        echo "Get your a** back to work!";\n    }\n}