class FileOutputter\n{\n    public function outputFile($file)\n    {\n        if (!file_exists($file)) {\n            throw new Exception("File not found.", 123);\n        }\n\n        try {\n            $contents = file_get_contents($file);\n        } catch (Exception $e) {\n            throw new Exception("Error reading file: " . $e->getMessage(), $e->getCode(), $e);\n        }\n\n        return $contents;\n    }\n}\n\n$fo = new FileOutputter;\ntry {\n    $contents = $fo->outputFile("File.extension");\n    echo $contents;\n} catch (Exception $e) {\n    // Handle the exception\n    echo $e->getMessage();\n    // Example of alternative execution flow based on exception code\n    if ($e->getCode() == 123) {\n        // Handle file not found specific logic\n        echo "Please check the file path and try again.";\n    }\n}